<template>
  <div class="related_topics_component">
    <h2 class="title">
        <span>相关池塘</span>
    </h2>

    <div class="x_scroller">
        <div class="scroller_label clear_float" :style="{width: scrollerWidth}">
          <div class="fl item" v-for="(item, index) in list" :key="item.id"
          @click="goTopicDetail(item.id)"
          v-if="!maxNum || (maxNum && index < maxNum)"
          :style="{backgroundImage: `url(${getBgImg(item) || defaultImg})`}">
            <p class="text">
                <span v-text="item.title"></span>
            </p>
          </div>
        </div>
    </div>
  </div>
</template>

<script type="text/javascript">
// import $ from 'jquery';
import { RouterUtil, CommonUtil } from '@/utils'
import TopicApi from './../topic-api.js'

export default {
  props: {
    list: Array,
    maxNum: Number
  },
  data() {
    return {
      playing: false,
      scrollerWidth: '',
      defaultImg: 'data:image/png;base64,'
    }
  },
  created() {
    this.getDefaultImg()
  },
  watch: {
    list() {
      this.setScrollerWidth()
    }
  },
  mounted() {
    this.setScrollerWidth()
  },
  methods: {
    getBgImg(item) {
      if (!item.imglist_info || !item.imglist_info[0]) {
        return this.defaultImg
      }

      return CommonUtil.fixImg(item.imglist_info[0].link, 'w=450&h=200')
    },
    getDefaultImg() {
      let params = {
        type: 'wap',
        code: 'home'
      }

      if (this.defaultImg) {
        return
      }

      TopicApi.getDefaultImg(params).then((res) => {
        if (res.code !== 200) {
          return
        }

        let index = 0;

        for (; index < res.data.length; index++) {
          if (res.data[index].alias === 'topic') {
            this.defaultImg = res.data[index].imgpic_info && CommonUtil.fixImg(res.data[index].imgpic_info.link, 'w=450&h=200')

            return
          }
        }
      });
    },
    setScrollerWidth() {
      let width = 0
      let count = this.list && this.list.length || 0

      if (this.maxNum && this.maxNum < count) {
        count = this.maxNum
      }

      width = count * 470 + 20

      this.scrollerWidth = width / 100 + 'rem'
    },
    goTopicDetail(id) {
      this.go(`/topic/detail?id=${id}`)
    },
    go(link) {
      RouterUtil.go(link, this.$router)
    }
  }
}
</script>

<style lang="less" scoped>
@import './main.less';
</style>
